/**
 * Returns array of predefined custom Newsletter forms
 * 
 * @return string
 */
function volcanno_get_newsletter_forms() {
    $submit_url = esc_attr( home_url( '/' ) . '?na=s' );

    $forms = array(
        'form_1' => '<div class="newsletter newsletter-widget">
                    <form method="post" action="' . $submit_url . '" onsubmit="return newsletter_check(this)">
                    <input type="hidden" name="nr" value="widget">
                    <input class="email" type="email" name="ne" placeholder="Subscribe to our newsletter...">
                    <input type="submit" class="submit" value="subscribe">
                    </form>
                    </div>'
    );

    return $forms;
}

